css.interactiveCSS:
h1 {
  color: red;
}
h2 {
  color: #0000FF;
}
h3 {
  color: rgb(0, 255, 0);
}
h4 {
  color: rgba(200,100,50,0.5);
}

h1 {
  background-color: blue;
}
h2 {
  background-image: url("/media/css/image.png");
}
h3 {
  background: linear-gradient(to right, red, blue);
}

h1 {
  background-color: lightblue;
  border: 5px solid orange;
}

div {
  width: 100px;
  height: 100px;
  background-color: lightblue;
}
p {
  width: 200px;
  height: 50px;
  background-color: lightgreen;
}

div {
  padding: 50px;
  background-color: lightblue;
}
p {
  margin: 50px;
  background-color: lightblue;
}

p {
  font-family: Arial, Helvetica, sans-serif;
  font-size: 20px;
  font-weight: bold;
  font-style: italic;
  font-variant: small-caps;
}

p {
  text-align: center;
  text-decoration: underline dotted;
  text-shadow: 0px 0px 5px black;
  line-height: 30px;
  letter-spacing: 5px;
}

p {
  background-color: lightblue;
  width: 100px;
  margin-inline: auto; 
}

.container {
  background-color: orange;
  height: 200px;
  width: 200px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  flex-wrap: wrap;
  gap: 10px;
}
.children {
  background-color: lightblue;
  text-align: center;
  width: 50px;
  height: 50px;
}

.element {
  background-color: orange;
  height: 100px;
  width: 100px;
  position: absolute;
  top: 30px;
  left: 30px;
}

.element:hover {
  background-color: red;
}
.element:active {
  background-color: green;
}
.element:first-child {
  background-color: blue;
}
.element:last-child {
  background-color: yellow;
}
.element:before {
  content: "Before element";
  background-color: gray;
  width: 100px;
}

.element {
  width: 100px;
  height: 100px;
  background-color: red;
  transition: background-color 0.5s 1s ease;
}
.element:hover {
  background-color: blue;
}

@keyframes myAnimation {
  0% {
    background-color: red;
  }
  50% {
    background-color: green;
  }
  100% {
    background-color: blue;
  }
}
.element {
  animation: myAnimation 2s ease 1s infinite alternate;
  width: 100px;
  height: 100px;
}

.element {
  width: 50px;
  height: 50px;
  font-size: 10px;
  background-color: red;
  transform: translate(100px, 50px) rotate(45deg) scale(2);
}

.element {
  width: 600px;
  background-color: blue;
}
@media (max-width: 600px) {
  .element {
    width: 200px;
    background-color: green;
  }  
}
